import { FiTrash } from 'react-icons/fi';
const Content = () => {
    const[item, setItem] = useState([
        {
            id:1,
            checked:false,
            item:"this is 1st item"
        },
        {
            id:2,
            checked:true,
            item:"this is 2st item"
        },
        {
            id:3,
            checked:false,
            item:"this is 3st item"
        }
    ]);
    const handelCheck = (id)=>{
       const listItems= item.map((item)=> item.id===id ?{...item , checked: !item.checked}:item);
       setItem(listItems);
       localStorage.setItem('shoppinglist',JSON.stringify(listItems));
    };
    const handelDelete=(id)=>{
    const listItems = item.filter((item)=>item.id!==id);
    setItem(listItems);
    localStorage.setItem('shoppinglist',JSON.stringify(listItems));
    }

  return (
    <main>
        {item.length?(
        <ul  >
    {item.map(item =>(
        <li   className='item' key={item.id}>
  <input type="checkbox"
  onChange={ ()=>handelCheck(item.id) }
  checked={item.checked} />
  <label
  style={(item.checked ) ? {textDecoration: 'line-through'}:null}
  onDoubleClick={()=>handelCheck(item.id)}
  >{item.item}</label>
  <FiTrash
  onClick={()=>handelDelete(item.id)}
  tabIndex="0"
  role="button"
  ></FiTrash>
        </li>
    ))}

        </ul>
        ):(
            <p style={{marginTop:'2rem'}}>your list is empty</p>
        )
        }
    </main>
  )
}

export default Content;
