  const[items, setItem] = useState([
    {
        id:1,
        checked:false,
        item:"this is 1st item"
    },
    {
        id:2,
        checked:true,
        item:"this is 2st item"
    },
    {
        id:3,
        checked:false,
        item:"this is 3st item"
    }
]);
